from django import formsfrom .models import UserAnswerclass AnswerForm(forms.ModelForm):    class Meta:        model = UserAnswer        fields = ['answer']        widgets = {            'answer': forms.Textarea(attrs={                'placeholder': 'Введите сюда ответ',                'rows': 6,                'class': 'w-full p-4 rounded-2xl shadow-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 resize-none bg-white text-gray-800',            }),        }